/*
 * customSelect v1.0.0
 */

"use strict"

;(function(){
	var plugin;

	var defaults = {
		// GENERAL
		className: "custom-select"
		// CALLBACKS
	};

	$.fn.customSelect = function( options ) {
		if(this.length == 0) return this;

		// support multiple elements
		if(this.length > 1) {
			this.each(function() {
				$(this).customSelect( options );
			});

			return this;
		}

		// create a name space to be througth the plugin
		var select = {};
		// set a reference to our select element
		var el = this.get(0),
			$el = this
		;

		/**
		 * ===================================================================================
		 * = PRIVATE FUNCTIONS
		 * ===================================================================================
		 */
		
		/**
		 * Initializes namespace settings to be used throughout plugin
		 */
		
		var _initialize = function() {
			// merge user-supplied options with defaults
			select.settings = $.extend({}, defaults, options);
			// set className for custom select
			select.className = select.settings.className;
			// select all option
			select.options = $el.children("option");

			_createCustomSelect();
		};

		/**
		 * Performs all DOM and CSS modifications
		 */
		
		var _createCustomSelect = function() {
			// hide native select
			el.style.display = "none";
			// create container for custom select
			var htmlSelect = "<div class='custom-select'>" +
							"<div class='custom-select__area'></div>" +
							"<div class='custom-select__down' style='display: none'></div>" +
						"</div>"
			;
			$(htmlSelect)
				.insertAfter(el)
			;
			// add options
			$.each(select.options, function(index) {
				var $this = $(this),
					text = this.innerHTML,
					item = "<div>" + text + "</div>"
				;

				if($this.is(":checked")) {
					$(".custom-select__area").append(item);
				} else {
					$(".custom-select__down").append(item);
				}

			});
		};

		// click
		var _click = function(e) {
			console.log("click");
			
			if($(".custom-select__down").is(":visible")) {
				$(".custom-select__down").hide();
			} else {
				$(".custom-select__down").show();
			}
		};

		$(".custom-select").on("click", _click);


		// initialize
		_initialize();

		// returns the current jQuery object
		return this
	}

})(jQuery);